    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        tab.setJogador1(criaJogador(1));
        tab.setJogador2(criaJogador(2));
        if (tab.verificaSimbolo(tab.getJogador1().getSimbolo(), tab.getJogador2().getSimbolo()))
            JOptionPane.showMessageDialog(null, "Simbolos iguais, mude o simbolo de algum jogador e se inscreva novamente");
        else {
            mudaBloqueio(false);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void posicaoA1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_posicaoA1ActionPerformed
        c++;
        if(quemVaiJogar(c)) {
            tab.a1 = tab.getJogador1().getSimbolo();
        } else {
            tab.a1 = tab.getJogador2().getSimbolo();
        }
        proximoPasso();
    }//GEN-LAST:event_posicaoA1ActionPerformed

