    private boolean quemVaiJogar(int contador) {
        return contador % 2 == 0;
    }

    private Jogador criaJogador(int qualJogador) {
        jogador = new Jogador();
        if (qualJogador == 1) {
            jogador.setNome(nomeJogador1.getText());
            jogador.setSimbolo(simboloJogador1.getText().charAt(0));   
        } else if (qualJogador == 2) {
            jogador.setNome(nomeJogador2.getText());
            jogador.setSimbolo(simboloJogador2.getText().charAt(0));
        }
        return jogador;
    }
    
    private void mudaBloqueio(boolean i) {
        //bloqueia para o usuário não se inscrever mais e libera o jogo
        nomeJogador1.setEnabled(i);
        nomeJogador2.setEnabled(i);
        simboloJogador1.setEnabled(i);
        simboloJogador2.setEnabled(i);
        jButton1.setEnabled(i);
        posicaoA1.setEnabled(!i);
        posicaoA2.setEnabled(!i);
        posicaoA3.setEnabled(!i);
        posicaoB1.setEnabled(!i);
        posicaoB2.setEnabled(!i);
        posicaoB3.setEnabled(!i);
        posicaoC1.setEnabled(!i);
        posicaoC2.setEnabled(!i);
        posicaoC3.setEnabled(!i);
    }

    private void proximoPasso(){
        desenhaBotoes();
        if(tab.verificaJogo() == "Vencedor !"){
            JOptionPane.showMessageDialog(null, tab.verificaJogo());
            tab.reiniciar();
        }
    }
    
    private void desenhaBotoes(){
        posicaoA1.setText(String.valueOf(tab.a1));
        posicaoA2.setText(String.valueOf(tab.a2));
        posicaoA3.setText(String.valueOf(tab.a3));
        posicaoB1.setText(String.valueOf(tab.b1));
        posicaoB2.setText(String.valueOf(tab.b2));
        posicaoB3.setText(String.valueOf(tab.b3));
        posicaoC1.setText(String.valueOf(tab.c1));
        posicaoC2.setText(String.valueOf(tab.c2));
        posicaoC3.setText(String.valueOf(tab.c3));
    }
    
